function searchMethod($token) {
    $curl = curl_init();
    $url = 'https://sandbox-hotelsearchapi.otelseasy.com/v1';
    $cityandcountry = explode(",", $_REQUEST['destination']);

    $dateRange = explode(" - ", $_REQUEST['daterangeResorts']);

    // print_r($_REQUEST['adults']);
    // exit();

    $Checkin = date('d-m-Y',strtotime($dateRange[0]));
    $Checkout = date('d-m-Y',strtotime($dateRange[1]));

    $checkin_date=date_create($Checkin);
    $checkout_date=date_create($Checkout);
    $no_of_days=date_diff($checkin_date,$checkout_date);
    $tot_days = $no_of_days->format("%a");


    if ($cityandcountry[0]=='Dubai City') {
         $cityandcountry[0] = 'Dubai';
    }

    $rooms = array();

    foreach ($_REQUEST['adults'] as $key => $value) {
       $rooms[$key]['adults'] = $value;
       $rooms[$key]['child'] = $_REQUEST['childs'][$key];
       if ($_REQUEST['childs'][$key]!=0) {
           for ($i=0; $i < $_REQUEST['childs'][$key]; $i++) { 
            if (isset($_REQUEST['childAge'][$key][$i])) {
                $rooms[$key]['Room'.($key+1).'ChildAge'][$i] = $_REQUEST['childAge'][$key][$i]; 
            }
           }
       }
    }

    $auth = array(
        'cityname' => trim($cityandcountry[0]," "),
        'countryname' => trim($cityandcountry[1]," "),
        'nationality' => 'India',
        'check_in' => $Checkin,
        'check_out' => $Checkout,
        'no_of_rooms' => count($_REQUEST['adults']),
        'Room' => $rooms,
    );

    $data = json_encode($auth);
    curl_setopt($curl, CURLOPT_POSTFIELDS, $data);
    curl_setopt($curl, CURLOPT_URL, $url);

    curl_setopt($curl, CURLOPT_HTTPHEADER, array(
      'Content-Type: application/json',
      'authorization: Bearer  '.$token.'',
    ));
    curl_setopt($curl, CURLOPT_RETURNTRANSFER, 1);
    curl_setopt($curl, CURLOPT_HTTPAUTH, CURLAUTH_BASIC);
    $result = curl_exec($curl);
    curl_close($curl);
    return $result;
}

